insert into contabilidade.planocontadetalhe values (9480, 1, '4.5.0.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS E DELEGAÇÕES RECEBIDAS', 'Compreende o somatório das variações patrimoniais aumentativas com transferências inter governamentais, transferências intragovernamentais, transferências de instituições multigovernamentais, transferências de instituições privadas com ou sem fins lucrativos, transferências de convênios, transferências do exterior e execuções orçamentárias delegadas.', 2, false, 0, 'N');
