insert into contabilidade.planocontadetalhe values (9491, 1, '4.5.1.4.2.01.00.00.00.00', 'RECURSOS PARA COBERTURA DE INSUFICIÊNCIAS FINANCEIRAS', 'Compreende os recursos orçamentários efetivamente utilizados para a cobertura de insuficiências financeiras entre as receitas e despesas previdenciárias no exercício de referência.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9492, 1, '4.5.1.5.0.00.00.00.00.00', 'TRANSFERÊNCIAS RECEBIDAS PARA APORTES DE RECURSOS PARA O SISTEMA DE PAGAMENTO DE PENSÕES MILITARES', 'Compreende o valor das transferências recebidas para os aportes financeiros do ente para cobertura de insuficiências financeiras, formação de reserva e cobertura de déficits financeiros ou atuariais do regime dos militares e outros aportes, exceto os decorrentes de alíquota de contribuição suplementar.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9493, 1, '4.6.2.2.1.02.00.00.00.00', 'GANHO LÍQUIDO COM A ALIENAÇÃO DE BENS IMÓVEIS', 'Compreende o ganho com alienação de ativo imobilizado, ou seja, quando o valor alienado do referido ativo e maior que o seu valor contábil, de maneira que a diferença compreende o ganho - Ganho Líquido com a Alienação de Bens Imóveis.', 2, false, 0, 'N');
