insert into contabilidade.planocontadetalhe values (9496, 1, '4.5.2.1.0.00.00.00.00.00', 'TRANSFERÊNCIAS CONSTITUCIONAIS E LEGAIS DE RECEITAS', 'Compreende as variações patrimoniais aumentativas decorrentes da transferência de outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na Constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9497, 1, '4.5.2.1.1.00.00.00.00.00', 'TRANSFERÊNCIAS CONSTITUCIONAIS E LEGAIS  DE RECEITAS- CONSOLIDAÇÃO', 'Registra as variações patrimoniais aumentativas decorrentes da transferência de outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na Constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS). Por exemplo, empresa independente de um ente recebendo transferência de outro ente (a empresa independente não pertence ao OFSS mas pertence ao conceito de governamental).', 2, false, 0, 'N');
